package SlidingWindow.LongestDuplicateSubstring_1044_Steven;

public class LongestDuplicateSubstring_1044_Steven {
  public static String longestDupSubstring(String s) {
    int size = s.length();
    String res = "";

    int i, j;
    for (int start = 0; start < size && start + res.length() <= size; start++) {
      int last = start + 1;

      i = start + Math.max(1, res.length());
      j = i + 1;

      while (start < size && last < size) {
        boolean flag = false;
        while (j <= size && s.substring(start, i).equals(s.substring(last, j))) {
          i++;
          j++;

          flag = true;
        }

        last++;
        j = last + (j - last);

        String candidate = s.substring(start, i - 1);
        res = res.length() >= candidate.length() ? res : candidate;

        if (flag) {
          i--;
        } else {
          j++;
        }
      }
    }

    return res;
  }

  public static void main(String[] args) {
    String s = "";
    System.out.println("Res: " + longestDupSubstring(s));
  }
}
